import axios from 'axios';
import { getSocket } from '../socket';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Log the baseURL for debugging
console.log('üîç API baseURL:', api.defaults.baseURL);

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers = config.headers || {};
    config.headers['Authorization'] = `Bearer ${token}`;
  }
  // Attach Socket.IO socket ID if available
  try {
    const socket = getSocket();
    if (socket && socket.id) {
      config.headers['x-socket-id'] = socket.id;
    }
  } catch (e) {
    // ignore if socket not ready
  }
  // Log all API requests
  console.log('üîç API Request:', config.method?.toUpperCase(), config.url, config.data);
  return config;
});

// Log all API responses
api.interceptors.response.use(
  (response) => {
    console.log('üîç API Response:', response.status, response.config.url);
    return response;
  },
  (error) => {
    console.error('üîç API Error:', error.response?.status, error.config?.url, error.message);
    return Promise.reject(error);
  }
);

export default api;
