import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import api from '../services/api';
import { Box, Typography, Paper } from '@mui/material';

const DeviceMap = () => {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchDevices() {
      try {
        const response = await api.get('/device/devices/registered');
        setDevices(response.data);
      } catch (err) {
        setError('Failed to fetch devices: ' + (err.response?.data?.message || err.message));
      }
    }
    fetchDevices();
  }, []);

  // Default map center (India)
  const center = [20.5937, 78.9629];

  return (
    <Paper sx={{ p: 2, mb: 2, borderRadius: 3, background: '#23273a', color: '#e3e6f3' }}>
      <Typography variant="h5" sx={{ color: '#00bcd4', fontWeight: 700, mb: 2 }}>
        Registered Devices Map
      </Typography>
      {error && <Typography color="error">{error}</Typography>}
      <Box sx={{ height: 400, width: '100%' }}>
        <MapContainer center={center} zoom={5} style={{ height: '100%', width: '100%', borderRadius: 12 }}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {devices.filter(d => d.latitude && d.longitude).map(device => (
            <Marker key={device._id || device.deviceId} position={[device.latitude, device.longitude]}>
              <Popup>
                <strong>{device.customName}</strong><br />
                Device ID: {device.deviceId}<br />
                Lat: {device.latitude}<br />
                Lon: {device.longitude}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </Box>
    </Paper>
  );
};

export default DeviceMap;
